import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';

function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const { darkMode, toggleDarkMode } = useTheme();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className={`navbar navbar-expand-lg ${darkMode ? 'dark' : 'navbar-light bg-light'}`}>
      <div className="container">
        <Link className="navbar-brand" to="/">Ana Sayfa</Link>
        <div className="navbar-nav">
          <button 
            className="theme-toggle-btn btn btn-outline-primary"
            onClick={toggleDarkMode}
          >
            {darkMode ? '‚òÄÔ∏è Aydƒ±nlƒ±k' : 'üåô Karanlƒ±k'}
          </button>
          {!token ? (
            <>
              <Link className="nav-link" to="/login">Giri≈ü Yap</Link>
              <Link className="nav-link" to="/register">Kayƒ±t Ol</Link>
            </>
          ) : (
            <button className="nav-link btn btn-link" onClick={handleLogout}>
              √áƒ±kƒ±≈ü Yap
            </button>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
